//  Escreva a declaração de uma variável do tipo booleano para saber se a pessoa é maior de idade.
package variaveis_cosntante;

public class MaiorIdade {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int pessoa = 19;
		
		if (pessoa >= 18) {
			System.out.println("Pessoa é maior de idade");
		} else {
			System.out.println("Pessoa é menor de idade");
		}
	}

}
